clear all;%initialisation des variables pour parametres graphe%load des donnees x,yrun('courbes.m');%nombre de courbesA = who('C*','');MARKER_SHAPE = '*';LINE_WIDTH = 1;MARKER_SIZE = 2;LINE_STYLE = '-';GRAPH_NAME = 'scan2.png';MATRIX_COLOR = [[0,0,1];[0,0,0];[1,0,0];];WAIT = 3; close all;%figure;hold on;  %titre du graphetitle(GRAPH_NAME);h=''; %permet de ploter les n courbesfor m=0 :length(A)-1        eval(['X = C' int2str(m) '(:,1);']);    eval(['Y = C' int2str(m) '(:,2);']);    plot(X,Y,strcat(MARKER_SHAPE, LINE_STYLE), LINE_WIDTH, MARKER_SIZE, 'color', MATRIX_COLOR(m+1,:));   h=strcat(h,strcat(strcat(strcat('''',strcat('courbe ', int2str(m+1))), ''''),',')); end;h=strcat('legend({',h,'});');eval([h]); %temps d'affichage du graphepause(WAIT); %creation du fichier pdfprint('-dpdf', strcat(GRAPH_NAME,'.pdf'));hold off;close all;